import React from 'react';
import { Route } from 'react-router-dom';
import { createStructuredSelector} from 'reselect';
import { connect } from 'react-redux';

import { fetchCollectionStartAync } from '../../redux/shop/shop.actions';
import {
  selectCollectionFetching,
  selectIsCollectionLoaded,
} from "../../redux/shop/shop.selectors";

import WithSpinner from '../../components/with-spinner/with-spinner.component';

import CollectionsOverview from '../../components/collections-overview/collections-overview.component';
import CollectionPage from '../collection/collection.component';
import  CollectionsOverviewContainer from '../collections-overview/collections-overview.container';
const CollectionPageWithSpinner = WithSpinner(CollectionPage);

class ShopPage extends React.Component {

  componentDidMount(){
      const { fetchCollectionStartAync } = this.props;
      fetchCollectionStartAync();
  }

  render() {
    const { match, isCollectionFetching, isCollectionLoaded } = this.props;

    return (
      <div className="shop-page">
        <Route
          exact
          path={`${match.path}`}
          render={(props) => (
            <CollectionsOverviewWithSpinner
              isLoading={isCollectionFetching}
              {...props}
            />
          )}
        />
        <Route
          path={`${match.path}/:collectionId`}
          render={(props) => (
            <CollectionPageWithSpinner
              isLoading={!isCollectionLoaded}
              {...props}
            />
          )}
        />
      </div>
    );
  }
} 

const mapStateToProps = createStructuredSelector({
  isCollectionFetching: selectCollectionFetching,
  isCollectionLoaded: selectIsCollectionLoaded
});

const mapDispatchToProps = dispatch => ({
  fetchCollectionStartAync: () => dispatch(fetchCollectionStartAync())
});

export default connect(mapStateToProps, mapDispatchToProps)(ShopPage);
