import { takeEvery } from 'redux-saga/effects';

import { firestore, convertCocllectionsSnapshotTomap } from '../../firebase/firebase.utils';

import {
    fetchCollectionSuccess, fetchCollectionsFailure
} from './shop.actions';

import ShopActionTypes from './shop.types';


export function* fetchCollectionsAsync(){
    yield console.log('I am fired');

    const collectionRef = firestore.collection("collections");


    collectionRef
      .get()
      .then((snapshot) => {
        const collectionsMap = convertCollectionsSanpshotToMap(snapshot);
        dispatch(fetchCollectionSuccess(collectionsMap));
      })
      .catch((error) => dispatch(fetchCollectionsFailure(error.message)));
};

export function* fetchCollectionsStart(){
    yield takeEvery(ShopActionTypes.FETCH_COLLECTION_START, fetchCollectionsAsync);
};
