import styled, { css } from 'styled-components';

const collectionItem = css`
  width: 22vw;
  display: flex;
  flex-direction: column;
  height: 350px;
  align-items: center;
  position: relative;
`;

export const CollectionItemContatiner = styled.div`
    ${collectionItem}
`;
