import styled, { css } from 'styled-components';

const collectionItem = css`
  width: 22vw;
  display: flex;
  flex-direction: column;
  height: 350px;
  align-items: center;
  position: relative;
`;

export const CollectionItemContatiner = styled.div`
    ${collectionItem}
`;

export const ImageContainer = styled.div`
    width: 100%;
    height: 95%;
    background-size: cover;
    background-position: center;
    margin-bottom: 5px;
`;

export const CollectionFooterContainer = styled.div`
         width: 100%;
         height: 5%;
         display: flex;
         justify-content: space-between;
         font-size: 18px;

         .name {
           width: 90%;
           margin-bottom: 15px;
         }

         .price {
           width: 10%;
         }
       `;
